import React, { useState } from 'react';
import './styles.css';
import { Button } from '@nextui-org/react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faInfo, faClose } from '@fortawesome/free-solid-svg-icons';
import ImageGallery from 'react-image-gallery';

const StaticProjectCard = ({ title, content, className }) => {
    return (
        <div className={`card ${className}`}>
            <div className="card-inner gradient-blue-purple shadow-lg rounded-lg overflow-hidden">
                <div className="card-front flex flex-col p-8 text-white">
                    <h1 className="text-5xl font-extrabold mb-4">{title}</h1>
                    <h3 className="text-2xl font-semibold mb-4">Faster Than Green</h3>
                    <p className="text-lg mb-6">{content}</p>
                </div>
            </div>
        </div>
    );
};

const FlippableProjectCard = ({ title, content, imageSrc, gradientClassName, className }) => {
    const [flipped, setFlipped] = useState(false);

    return (
        <div
            className={`card ${flipped ? 'flipped' : ''} ${className}`}
            onClick={() => setFlipped(!flipped)}>
            <div className="card-inner">
                <div className={`card-front p-5 ${gradientClassName} shadow-lg rounded-lg overflow-hidden`}>
                    <img src={imageSrc} alt={title} className="object-scale-down w-full h-full rounded-lg" />
                    <Button className='absolute bottom-2 right-2 rounded-full' size='sm' isIconOnly onClick={() => setFlipped(!flipped)} variant='faded' color='primary'>
                        <FontAwesomeIcon icon={faInfo} />
                    </Button>
                </div>
                <div className={`card-back p-5 ${gradientClassName} shadow-lg rounded-lg overflow-hidden`}>
                    <h2 className="text-xl font-semibold mb-2 text-black">{title}</h2>
                    <p className="text-gray-900">{content}</p>
                    <Button className='absolute bottom-2 right-2 rounded-full' size='sm' isIconOnly onClick={() => setFlipped(!flipped)} variant='faded' color='primary'>
                        <FontAwesomeIcon icon={faClose} />
                    </Button>
                </div>
            </div>
        </div>
    );
};

const TapGame = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [currentImageIndex, setCurrentImageIndex] = useState(0);
    const images = [
        {
            original: require('../../../../assets/ftg/ftg1.png'),
            thumbnail: require('../../../../assets/ftg/ftg1.png')
        },
        {
            original: require('../../../../assets/ftg/ftg2.png'),
            thumbnail: require('../../../../assets/ftg/ftg2.png')
        },
        {
            original: require('../../../../assets/ftg/ftg3.png'),
            thumbnail: require('../../../../assets/ftg/ftg3.png')
        },
        {
            original: require('../../../../assets/ftg/ftg4.png'),
            thumbnail: require('../../../../assets/ftg/ftg4.png')
        },
        {
            original: require('../../../../assets/ftg/ftg5.png'),
            thumbnail: require('../../../../assets/ftg/ftg5.png')
        },
        {
            original: require('../../../../assets/ftg/ftg6.png'),
            thumbnail: require('../../../../assets/ftg/ftg6.png')
        },
    ];

    const handleImageClick = (index) => {
        setCurrentImageIndex(index);
        setIsOpen(true);
    };

    const closeModal = () => {
        setIsOpen(false);
    };

    return (
        <div className="h-screen grid grid-cols-5 grid-rows-5 gap-4 py-4 px-10">
            <StaticProjectCard
                title="Faster Than Green"
                content="Faster Than Green is a fast-paced mobile game that tests your reaction time and accuracy. The objective of the game is to score as many points as possible by tapping only the green squares on a 5x5 grid. The game starts with a 5x5 grid with 12 green squares positioned randomly, and when you tap a green square, a red square turns into a green one and the one you pressed becomes a red one. Be careful not to tap the red squares, as they will subtract 1 point from your score. The game ends when 20 seconds pass."
                className="col-span-5 row-span-2"
            />
            <FlippableProjectCard
                title="Web Version"
                content="The web version was made in pure HTML, CSS, JS as a base idea for the React Native version. In the web version there is an online leaderboard made for learning Firebase and more game mechanics such as the gold square, which shows up randomly for a few milliseconds and gives you 5 points, and grey squares that remove 5 seconds."
                gradientClassName="gradient-teal-cyan"
                imageSrc={require('../../../../assets/ftg/ftg_web.png')}
                className="col-span-3 row-span-2"
            />
            <div className="card col-span-2 row-span-2">
                <div className="card-inner gradient-indigo-purple shadow-lg rounded-lg overflow-hidden">
                    <div className="card-front flex flex-col p-8 text-white">
                        <h1 className="text-5xl font-extrabold mb-4">Details</h1>
                        <p className="text-lg mb-6">
                            The React Native version of the game is currently lost but will include all the web version's features plus more in the future.
                        </p>
                    </div>
                </div>
            </div>
            <div className="col-span-3 row-span-3">
                <div className="card w-full h-full">
                    <div className="card-inner flex justify-center items-center p-5 gradient-yellow-green rounded-lg shadow-lg overflow-hidden flex-col">
                        <h1 className="text-5xl font-extrabold text-white absolute top-8 left-5">Screenshots</h1>
                        <ImageGallery
                            items={images}
                            showPlayButton={false}
                            showFullscreenButton={true}
                            showBullets={true}
                            showThumbnails={false}
                            showIndex={false}
                            showNav={true}
                        />
                    </div>
                </div>
            </div>
            <div className="card col-span-2 row-span-3">
                <div className="card-inner gradient-darkred-red shadow-lg rounded-lg overflow-hidden">
                    <div className="card-front flex flex-col p-8 text-white">
                        <h1 className="text-5xl font-extrabold mb-4">More Features</h1>
                        <ul className="list-disc list-inside text-lg space-y-2">
                            <li>Gold square: appears randomly, gives 5 points.</li>
                            <li>Grey square: appears randomly, removes 5 seconds.</li>
                            <li>Online leaderboard: built with Firebase.</li>
                            <li>React Native version (coming soon).</li>
                        </ul>
                    </div>
                </div>
            </div>
            <div className="card col-span-5 row-span-1">
                <div className="card-inner gradient-darkgreen-teal shadow-lg rounded-lg overflow-hidden">
                    <div className="card-front flex flex-col p-8 text-white">
                        <h1 className="text-5xl font-extrabold mb-4">Play the Game</h1>
                        <a href="https://your-game-link.com" className="text-lg underline">
                            You can play the web version here
                        </a>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default TapGame;
