import React from 'react';
import { useState, useEffect } from 'react';
import { Select, SelectItem, Input, Button, Card, CardBody, DatePicker, DateRangePicker, Chip } from '@nextui-org/react';
import { CalendarDate, parseDate } from "@internationalized/date";
import { jwtDecode } from 'jwt-decode';
const Home = () => {
    const apiUrl = process.env.REACT_APP_DEV_API;
    // expense = OUT, income = IN
    /*{
 "user": 2,
 "amount": 299.00,
 "transaction_type": "OUT",
 "description": "Salary",
 "date": "2024-08-01",
 "tags": [
  {
   "name": "bank"
  }
 ]
}*/
    const [amount, setAmount] = useState('');
    const [transactionType, setTransactionType] = useState('OUT');
    const [description, setDescription] = useState('');
    const [date, setDate] = useState(calculateTodayDate());
    const [tags, setTags] = useState('');
    const [transactions, setTransactions] = useState([]);
    const [editableTransactionId, setEditableTransactionId] = useState(null);

    const [xDays, setXDays] = useState(10);


    useEffect(() => {
        loadLastXDaysTransactions()
    }, []);

    const handleTransaction = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token');
        const userId = jwtDecode(token).user_id
        const response = await fetch(`${apiUrl}/transactions/`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            },
            body: JSON.stringify({
                user: userId,
                amount,
                transaction_type: transactionType,
                description,
                date: date.toString(),
                tags: [{ name: tags }]
            })
        });
        const data = await response.json();
        if (data.token) {
            window.location.href = '/login';
        } else {

        }

        loadLastXDaysTransactions();
    }

    const loadTransactions = async () => {
        const token = localStorage.getItem('token');
        const response = await fetch(`${apiUrl}/transactions/logic/history/current`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            }
        });
        const data = await response.json();
        if (data.token) {
            window.location.href = '/login';
        } else {
            var dateSortedData = data.sort((a, b) => new Date(b.date) - new Date(a.date));
            var preciseSortedData = dateSortedData.sort((a, b) => b.id - a.id);
            setTransactions(data);
        }
    }

    const loadLastXDaysTransactions = async (days = xDays) => {
        const token = localStorage.getItem('token');
        const response = await fetch(`${apiUrl}/transactions/logic/history/last/${days}`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            }
        });
        const data = await response.json();
        if (data.token) {
            window.location.href = '/login';
        } else {
            data.sort((a, b) => b.id - a.id);
            setTransactions(data);
        }
    }

    const handleDelete = (transaction) => async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token');
        const response = await fetch(`${apiUrl}/transactions/${transaction.id}`, {
            method: 'DELETE',
            headers: {
                'Authorization': `Bearer ${token}`
            }
        });
        loadLastXDaysTransactions();
    }

    const handleEditToggle = (transactionId) => {
        setEditableTransactionId(editableTransactionId === transactionId ? null : transactionId);
    }

    //handleEditSave(transaction, localDescription, localAmount, localDate)
    const handleEditSave = async (transaction, localDescription, localAmount, localDate) => {
        const token = localStorage.getItem('token');
        const userId = jwtDecode(token).user_id;
        const date = localDate.year + '-' + localDate.month + '-' + localDate.day;
        const response = await fetch(`${apiUrl}/transactions/${transaction.id}/`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            },
            body: JSON.stringify({
                user: userId,
                amount: localAmount,
                transaction_type: transaction.transaction_type,
                description: localDescription,
                date: date,
                tags: transaction.tags
            })
        });
        const data = await response.json();
        if (data.token) {
            window.location.href = '/login';
        } else {
        }

        loadLastXDaysTransactions();
        setEditableTransactionId(null);
    }

    const handleTagDelete = (tranasctionId, tagId) => async (e) => {
        const token = localStorage.getItem('token');
        const response = await fetch(`${apiUrl}/transactions/logic/${tranasctionId}/tag/${tagId}`, {
            method: 'DELETE',
            headers: {
                'Authorization': `Bearer ${token}`
            }
        });
        loadLastXDaysTransactions();
    }

    const TransactionList = ({ transactions }) => {
        const [localTransactions, setLocalTransactions] = useState(transactions);

        useEffect(() => {
            setLocalTransactions(transactions);
        }, [transactions]);

        const handleDelete = (transaction) => {
            setLocalTransactions((prevTransactions) =>
                prevTransactions.filter((t) => t.id !== transaction.id)
            );
        };
        return (
            <div className="flex flex-col p-4">
                {transactions.slice(0, 100).map((transaction) => (
                    <TransactionCard
                        key={transaction.id}
                        transaction={transaction}
                        editable={editableTransactionId === transaction.id}
                        onSave={(transaction, localDescription, localAmount, localDate) => handleEditSave(transaction, localDescription, localAmount, localDate)}
                        onEditToggle={() => handleEditToggle(transaction.id)}
                    />
                ))}
            </div>
        );
    };
    const TransactionCard = ({ transaction, editable, onDelete, onEditToggle, onSave }) => {
        const [localDescription, setLocalDescription] = useState(transaction.description);
        const [localAmount, setLocalAmount] = useState(transaction.amount);
        const [localDate, setLocalDate] = useState(parseDate(transaction.date));
        const [newTagInputVisible, setNewTagInputVisible] = useState(false);
        const [newTagName, setNewTagName] = useState('');
        const [isDisintegrating, setIsDisintegrating] = useState(false);

        useEffect(() => {
            setLocalDescription(transaction.description);
            setLocalAmount(transaction.amount);
            setLocalDate(parseDate(transaction.date));
        }, [editable, transaction.description, transaction.amount, transaction.date]);

        const handleDeleteAnimation = (transaction) => async (e) => {
            setIsDisintegrating(true);
            setTimeout(() => {
                handleDelete(transaction)(e);
            }, 800);
        }
        const handleNewTagSubmit = async (e) => {
            if (e.key === 'Enter' && newTagName.trim() !== '') {
                const token = localStorage.getItem('token');
                const response = await fetch(`${apiUrl}/transactions/logic/${transaction.id}/tag`, {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify({ name: newTagName.trim() })
                });
                setNewTagName('');
                setNewTagInputVisible(false);
                loadLastXDaysTransactions();
            }
        };

        return (
            <div className={`transaction-card flex flex-col p-4 my-4 rounded-lg shadow-md border-3 ${transaction.transaction_type === 'IN' ? 'border-green-200' : 'border-red-200'} ${isDisintegrating ? 'fragment' : ''}`}>
                <div className="flex justify-between items-center mb-4">
                    <Input
                        variant={editable ? 'bordered' : 'flat'}
                        label='Titolo'
                        className={`text-lg font-semibold w-1/2 mr-2`}
                        value={localDescription}
                        onChange={(e) => setLocalDescription(e.target.value)}
                        isReadOnly={!editable}
                    />
                    <Input
                        variant={editable ? 'bordered' : 'flat'}
                        label='Importo'
                        type='number'
                        className="text-lg font-semibold w-1/4"
                        value={localAmount}
                        onChange={(e) => setLocalAmount(e.target.value)}
                        isReadOnly={!editable}
                    />
                </div>
                <DatePicker
                    variant={editable ? 'bordered' : 'flat'}
                    label='Data'
                    className="mb-4 w-full"
                    value={localDate}
                    onChange={(newDate) => setLocalDate(newDate)}
                    isReadOnly={!editable}
                />
                <div className="grid grid-cols-4 gap-4">
                    <div className="flex flex-wrap mb-4 col-span-3 gap-2">
                        {transaction.tags.map((tag) => (
                            tag.name !== '' && tag.name !== null && tag.name !== undefined &&
                            <Chip
                                key={tag.id}
                                color="primary"
                                onClose={handleTagDelete(transaction.id, tag.id)}
                                style={{ backgroundColor: colorTag(tag.name) }}
                            >{tag.name}</Chip>
                        ))}
                        {newTagInputVisible ? (
                            <Input
                                value={newTagName}
                                onChange={(e) => setNewTagName(e.target.value)}
                                onKeyDown={handleNewTagSubmit}
                                onBlur={() => setNewTagInputVisible(false)}
                                autoFocus
                            />
                        ) : (
                            <Chip
                                onClick={() => setNewTagInputVisible(true)}
                                className='cursor-pointer'>+</Chip>
                        )}
                    </div>
                    <div className="flex flex-col gap-2 cols-span-1">
                        <Button
                            color="primary"
                            onClick={onEditToggle}
                            className={`${!editable ? 'block' : 'hidden'}`}
                        >Modifica</Button>
                        <Button
                            color="secondary"
                            onClick={() => onSave(transaction, localDescription, localAmount, localDate)}
                            className={`${editable ? 'block' : 'hidden'}`}
                        >Salva</Button>
                        <Button color="danger" onClick={handleDeleteAnimation(transaction)}>Elimina</Button>
                    </div>
                </div>
            </div>
        );
    };


    return (
        <div className='w-screen h-screen flex flex-col p-3'>
            <h1 className='text-4xl font-bold'>ðŸ’¸ Soldini</h1>
            <div className="grid grid-cols-4 flex-grow gap-3 p-3">
                <div className="col-span-2 flex flex-col h-full p-3 gap-3">
                    <div>
                        <h2 className='text-2xl font-bold pb-3'>Transazione nuova</h2>
                        <div className='flex grid-cols-7 gap-2'>
                            <Input
                                label='Importo'
                                type='number'
                                variant='bordered'
                                value={amount}
                                isRequired={true}
                                onChange={(e) => setAmount(e.target.value)}
                            />
                            <Select
                                label='Tipo'
                                isRequired={true}
                                variant='bordered'
                                value={transactionType}
                                color={transactionType === 'IN' ? 'success' : 'danger'}
                                defaultSelectedKeys={['OUT']}
                                disallowEmptySelection={true}
                                onChange={(e) => setTransactionType(e.target.value)}
                            >
                                <SelectItem
                                    label='Tipo'
                                    value='IN'
                                    key='IN'
                                >Entrata</SelectItem>
                                <SelectItem
                                    label='Tipo'
                                    value='OUT'
                                    key='OUT'
                                >Uscita</SelectItem>

                            </Select>
                            <Input
                                label='Titolo'
                                type='text'
                                variant='bordered'
                                isRequired={true}
                                value={description}
                                onChange={(e) => setDescription(e.target.value)}
                            />
                            <DatePicker
                                label='Data'
                                variant='bordered'
                                defaultValue={calculateTodayDate}
                                isRequired={true}
                                value={date}
                                onChange={(newDate) => setDate(newDate)}
                            />
                            <Input
                                label='Tags'
                                type='text'
                                variant='bordered'
                                value={tags}
                                onChange={(e) => setTags(e.target.value)}
                            />
                            <Button
                                label='Aggiungi'
                                color='primary'
                                style={{ height: 'auto' }}
                                onClick={handleTransaction}
                            >Aggiungi</Button>
                        </div>
                    </div>
                    <div className='overflow-y-auto flex-grow' style={{ height: '50vh' }}>
                        <div class="flex flex-row">
                            <h2 className='text-2xl font-bold pb-3'>Transazioni recenti - ultimi </h2>
                            <Select variant='bordered' labelPlacement='inside' aria-label="giorni" disallowEmptySelection={true} className='ml-3 w-20 h-fit' value={xDays} defaultSelectedKeys={["10"]} onChange={(e) => setXDays(e.target.value)}>
                                <SelectItem key='10' label='10' onClick={() => { setXDays(10); loadLastXDaysTransactions(10) }}>10</SelectItem>
                                <SelectItem key='20' label='20' onClick={() => { setXDays(20); loadLastXDaysTransactions(20) }}>20</SelectItem>
                                <SelectItem key='30' label='30' onClick={() => { setXDays(30); loadLastXDaysTransactions(30) }}>30</SelectItem>
                            </Select>
                            <h2 className='text-2xl font-bold pb-3 ms-3'>giorni</h2>
                        </div>
                        <div className="transactions-container">
                            <TransactionList transactions={transactions} />
                        </div>
                    </div>
                </div>
                <div className="col-span-2 flex flex-col h-full p-3">
                    {/* this part can see the month or year summary,, so after the h2, there must be a choise */}
                    <h2 className='text-2xl font-bold'>Riepilogo</h2>
                    <Select label='Riepilogo'>
                        <SelectItem label='Riepilogo' value='month'>Mese</SelectItem>
                        <SelectItem label='Riepilogo' value='year'>Anno</SelectItem>
                    </Select>
                </div>
            </div>
        </div>
    );
};

function colorTag(tag) {
    // color generator based on the tag name
    var hash = 0;
    for (var i = 0; i < tag.length; i++) {
        hash = tag.charCodeAt(i) + ((hash << 5) - hash);
    }
    var c = (hash & 0x00FFFFFF)
        .toString(16)
        .toUpperCase();
    return "#" + "00000".substring(0, 6 - c.length) + c;

}

function calculateTodayDate() {
    const pad = (num, places) => String(num).padStart(places, '0');
    var date = new Date();
    var day = date.getDate();
    var month = date.getMonth();
    var year = date.getFullYear();
    var dateString = year + "-" + pad((month + 1), 2) + "-" + pad(day, 2);
    return parseDate(dateString);
}



