import React, { useState } from 'react';
import { Button } from '@nextui-org/react';
import './styles.css';  // Assicurati di avere un file CSS per gestire le animazioni di rotazione

const ProjectCard = ({ title, content, imageSrc }) => {
    const [flipped, setFlipped] = useState(false);

    return (
        <div
            className={`card ${flipped ? 'flipped' : ''}`}
            onClick={() => setFlipped(!flipped)}
        >
            <div className="card-inner">
                <div className="card-front">
                    <h1 className="text-2xl font-bold">{title}</h1>
                    <img src={imageSrc} alt={title} className="object-cover w-full h-full" />
                </div>
                <div className="card-back">
                    <h2 className="text-xl font-semibold mb-2">{title}</h2>
                    <p className="text-gray-700">{content}</p>
                </div>
            </div>
        </div>
    );
};

const LTMS = () => {
    return (
        <div className="h-screen grid grid-cols-2 md:grid-cols-4 gap-6 px-12 py-6">
            <ProjectCard
                title="Project 1"
                content="Details about project 1"
                imageSrc="https://via.placeholder.com/200"
            />
            <ProjectCard
                title="Project 2"
                content="Details about project 2"
                imageSrc="https://via.placeholder.com/200"
            />
            <ProjectCard
                title="Project 3"
                content="Details about project 3"
                imageSrc="https://via.placeholder.com/200"
            />
            <ProjectCard
                title="Project 4"
                content="Details about project 4"
                imageSrc="https://via.placeholder.com/200"
            />
            {/* Aggiungi altre ProjectCard se necessario */}
        </div>
    );
};

export default LTMS;
