import React from 'react';
import { useState, useEffect } from 'react';
import { Navbar, NavbarBrand, NavbarContent, NavbarItem, Link, Button, NavbarMenu, NavbarMenuItem, NavbarMenuToggle } from '@nextui-org/react';
import { LocoRocoLogo } from '../../assets/locoroco.png';

const Nav = () => {
    const [active, setActive] = useState('');
    const [isMenuOpen, setIsMenuOpen] = React.useState(false);

    useEffect(() => {
        const path = window.location.pathname;
        setActive(path.slice(1));
    }, []);

    {/*
        <li className={active === 'chi_e' ? 'active' : ''}><a href="/chi_e">Chi sono</a></li>
        <li className={active === 'cosa_ha_fatto' ? 'active' : ''}><a href="/cosa_ha_fatto">Portfolio</a></li>
        <li className={active === 'curriculum' ? 'active' : ''}><a href="/curriculum">Curriculum</a></li>
    */}
    return (
        <Navbar onMenuOpenChange={setIsMenuOpen}>
            <NavbarContent>
                <NavbarMenuToggle
                    aria-label={isMenuOpen ? "Close menu" : "Open menu"}
                    className="sm:hidden"
                />
                <NavbarBrand>
                    <img src={require('../../assets/locoroco.png')} alt="LocoRoco Logo" className="w-10 h-10" />
                    <p className="font-bold text-inherit">Mariooooooo</p>
                </NavbarBrand>
            </NavbarContent>

            <NavbarContent className="hidden sm:flex gap-4" justify="center">
                <NavbarItem>
                    <Link href="/chi_e">
                        Home
                    </Link>
                </NavbarItem>
                <NavbarItem isActive>
                    <Link href="/cosa_ha_fatto">
                        Portfolio
                    </Link>
                </NavbarItem>
                <NavbarItem>
                    <Link href="/curriculum">
                        Curriculum
                    </Link>
                </NavbarItem>
            </NavbarContent>

            <NavbarMenu>
                <NavbarMenuItem href="/chi_e" active={active === 'chi_e'}>Chi sono</NavbarMenuItem>
                <NavbarMenuItem href="/cosa_ha_fatto" active={active === 'cosa_ha_fatto'}>Portfolio</NavbarMenuItem>
                <NavbarMenuItem href="/curriculum" active={active === 'curriculum'}>Curriculum</NavbarMenuItem>
            </NavbarMenu>
        </Navbar>

    )
};



export default Nav;
