import React from 'react';
import { useState, useEffect } from 'react';
const Nav = () => {
    const [active, setActive] = useState('');

    useEffect(() => {
        const path = window.location.pathname;
        setActive(path.slice(1));
    }, []);

    return (
        <nav>
            <ul>
                {/* Chi sono, portfolio, curriculum */}
                <li className={active === 'chi_e' ? 'active' : ''}><a href="/home">Chi sono</a></li>
                <li className={active === 'cosa_ha_fatto' ? 'active' : ''}><a href="/about">Portfolio</a></li>
                <li className={active === 'curriculum' ? 'active' : ''}><a href="/curriculum">Curriculum</a></li>
            </ul>
        </nav>
    )
};



export default Nav;
