import React from 'react';
import { useState, useEffect } from 'react';
import { Select, SelectItem, Input, Button, Card, CardBody, DatePicker, DateRangePicker } from '@nextui-org/react';
import { CalendarDate, parseDate } from "@internationalized/date";
import { jwtDecode } from 'jwt-decode';
const Home = () => {
    const apiUrl = process.env.REACT_APP_DEV_API;
    // expense = OUT, income = IN
    /*{
 "user": 2,
 "amount": 299.00,
 "transaction_type": "OUT",
 "description": "Salary",
 "date": "2024-08-01",
 "tags": [
  {
   "name": "bank"
  }
 ]
}*/
    const [amount, setAmount] = useState('');
    const [transactionType, setTransactionType] = useState('OUT');
    const [description, setDescription] = useState('');
    const [date, setDate] = useState(calculateTodayDate());
    const [tags, setTags] = useState('');
    const [transactions, setTransactions] = useState([]);

    const [xDays, setXDays] = useState(10);


    useEffect(() => {
        loadLastXDaysTransactions()
    }, []);

    const handleTransaction = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token');
        const userId = jwtDecode(token).user_id
        const response = await fetch(`${apiUrl}/transactions/`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            },
            body: JSON.stringify({
                user: userId,
                amount,
                transaction_type: transactionType,
                description,
                date: date.toString(),
                tags: [{ name: tags }]
            })
        });
        const data = await response.json();
        if (data.token) {
            window.location.href = '/login';
        } else {
            console.log(xDays)
            console.log('Transaction failed');
        }

        loadLastXDaysTransactions();
    }

    const loadTransactions = async () => {
        const token = localStorage.getItem('token');
        const response = await fetch(`${apiUrl}/transactions/logic/history/current`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            }
        });
        const data = await response.json();
        if (data.token) {
            window.location.href = '/login';
        } else {
            var dateSortedData = data.sort((a, b) => new Date(b.date) - new Date(a.date));
            var preciseSortedData = dateSortedData.sort((a, b) => b.id - a.id);
            setTransactions(data);
        }
    }

    const loadLastXDaysTransactions = async (days = xDays) => {
        const token = localStorage.getItem('token');
        const response = await fetch(`${apiUrl}/transactions/logic/history/last/${days}`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            }
        });
        const data = await response.json();
        if (data.token) {
            window.location.href = '/login';
        } else {
            console.log(xDays)
            data.sort((a, b) => b.id - a.id);
            setTransactions(data);
        }
    }

    const handleDelete = (transaction) => async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token');
        const response = await fetch(`${apiUrl}/transactions/${transaction.id}`, {
            method: 'DELETE',
            headers: {
                'Authorization': `Bearer ${token}`
            }
        });
        loadLastXDaysTransactions();
    }

    const handleEdit = (transaction) => async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token');
        const response = await fetch(`${apiUrl}/transactions/${transaction.id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            },
            body: JSON.stringify({
                user: transaction.user,
                amount: transaction.amount,
                transaction_type: transaction.transaction_type,
                description: transaction.description,
                date: transaction.date,
                tags: transaction.tags
            })
        });
        const data = await response.json();
        if (data.token) {
            window.location.href = '/login';
        } else {
            console.log('Transaction edited');
        }
        loadLastXDaysTransactions();
    }


    const TransactionList = ({ transactions, onDelete, onEdit }) => {
        return (
            <div className="flex flex-col p-4">
                {transactions.slice(0, 100).map((transaction) => (
                    <TransactionCard
                        key={transaction.id}
                        transaction={transaction}
                    />
                ))}
            </div>
        );
    };

    const TransactionCard = ({ transaction, onDelete, onEdit }) => {
        return (
            <div className={`flex flex-col p-4 my-4 rounded-lg shadow-md border-3 ${transaction.transaction_type === 'IN' ? 'border-green-200' : 'border-red-200'}`}>
                <div className="flex justify-between items-center mb-4">
                    <Input variant='underlined' className="text-lg font-semibold w-1/2 mr-2" value={transaction.description} disabled={true} />
                    <Input variant='underlined' className="text-lg font-semibold w-1/4" value={transaction.amount} disabled={true} />
                </div>
                <DatePicker variant='underlined' className="mb-4 w-full" value={parseDate(transaction.date)} disabled={true} />
                <div className="grid grid-cols-4">
                    <div className="flex flex-wrap mb-4 col-span-3">
                        {transaction.tags.map((tag) => (
                            <>
                            <span className="bg-purple-200 rounded-lg px-2 py-1 mx-1">{tag.name}</span>
                            <span className="bg-purple-200 rounded-lg px-2 py-1 mx-1">{tag.name}</span>


                            <span className="bg-purple-200 rounded-lg px-2 py-1 mx-1">{tag.name}</span>

                            <span className="bg-purple-200 rounded-lg px-2 py-1 mx-1">{tag.name}</span>

                            <span className="bg-purple-200 rounded-lg px-2 py-1 mx-1">{tag.name}</span>

v                            <span className="bg-purple-200 rounded-lg px-2 py-1 mx-1">{tag.name}</span>


                            </>
                        ))}
                    </div>
                    <div className="flex justify-end space-x-2 col-span-1">
                        <Button color="primary" onClick={handleEdit(transaction)}>Modifica</Button>
                        <Button color="primary" onClick={handleDelete(transaction)}>Elimina</Button>
                    </div>
                </div>
            </div>
        );
    };



    return (
