import React from 'react';
import { useState, useEffect } from 'react';
const Nav = () => {
    const [active, setActive] = useState('home');

    useEffect(() => {
        const path = window.location.pathname;
        setActive(path.slice(1));
    }, []);


    return (
        <nav>
            <ul>
                <li>
                    <a href="/home">Home</a>
                </li>
                <li>
                    <a href="/about">About</a>
                </li>
                <li>
                    <a href="/contact">Contact</a>
                </li>
            </ul>
        </nav>
    )
};



export default Nav;
