import React, { useState, useEffect, useRef } from 'react';
import { Select, SelectItem, Input, Button, Card, CardBody, DatePicker, DateRangePicker, Chip } from '@nextui-org/react';
import { CalendarDate, parseDate } from "@internationalized/date";
import Land from './Pages/Land';
import Progetto1 from './Pages/Progetto1';
import Progetto2 from './Pages/Progetto2';

const Home = () => {
    const [amount, setAmount] = useState('');
    const containerRef = useRef(null);

    useEffect(() => {
        const handleScroll = (event) => {
            if (containerRef.current) {
                const container = containerRef.current;
                const page2 = document.getElementById('page2');
                const page3 = document.getElementById('page3');

                // Check if we are at the section of Progetto1 or Progetto2
                if (window.scrollY >= page2.offsetTop - window.innerHeight && window.scrollY < page3.offsetTop - window.innerHeight) {
                    container.style.overflowY = 'hidden';
                    container.style.overflowX = 'scroll';
                } else {
                    container.style.overflowY = 'scroll';
                    container.style.overflowX = 'hidden';
                }
            }
        };

        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    return (
        <div ref={containerRef} className="snap-y snap-mandatory overflow-y-scroll overflow-x-hidden h-screen flex-grow z-0">
            <div className="snap-always snap-center h-screen" id="page1">
                <Land />
            </div>
            <div className="snap-always snap-center h-screen flex" id="page2">
                <Progetto1 />
            </div>
            <div className="snap-always snap-center h-screen flex" id="page3">
                <Progetto2 />
            </div>
        </div>
    );
};

export default Home;
