import React, { useState, useEffect } from 'react';
import { Navbar, NavbarBrand, NavbarContent, NavbarItem, Link, Button, NavbarMenu, NavbarMenuItem, NavbarMenuToggle } from '@nextui-org/react';
import LocoRocoLogo from '../../assets/locoroco.png';

const Nav = () => {
    const [active, setActive] = useState('');
    const [isMenuOpen, setIsMenuOpen] = useState(false);

    useEffect(() => {
        const path = window.location.pathname;
        setActive(path.slice(1));
    }, []);

    return (
        <Navbar isBordered variant="sticky" css={{ borderRadius: '25px', margin: '20px auto', width: 'fit-content', padding: '10px 20px' }}>
            <NavbarBrand>
                <img src={LocoRocoLogo} alt="LocoRoco Logo" style={{ width: '50px', marginRight: '10px' }} />
            </NavbarBrand>
            <NavbarContent>
                <NavbarItem isActive={active === 'chi_e'}>
                    <Link href="/chi_e" color="text">Chi è</Link>
                </NavbarItem>
                <NavbarItem isActive={active === 'cosa_ha_fatto'}>
                    <Link href="/cosa_ha_fatto" color="text">Cosa ha fatto</Link>
                </NavbarItem>
                <NavbarItem isActive={active === 'curriculum'}>
                    <Link href="/curriculum" color="text">Curriculum</Link>
                </NavbarItem>
            </NavbarContent>
            <NavbarMenuToggle showIn="xs" onClick={() => setIsMenuOpen(!isMenuOpen)} />
            <NavbarMenu isOpen={isMenuOpen} onClose={() => setIsMenuOpen(false)}>
                <NavbarMenuItem isActive={active === 'chi_e'}>
                    <Link href="/chi_e">Chi è</Link>
                </NavbarMenuItem>
                <NavbarMenuItem isActive={active === 'cosa_ha_fatto'}>
                    <Link href="/cosa_ha_fatto">Cosa ha fatto</Link>
                </NavbarMenuItem>
                <NavbarMenuItem isActive={active === 'curriculum'}>
                    <Link href="/curriculum">Curriculum</Link>
                </NavbarMenuItem>
            </NavbarMenu>
        </Navbar>
    );
};

export default Nav;
