import React, { useState } from 'react';
import ImageGallery from 'react-image-gallery';
import Modal from 'react-modal';
import './styles.css';
import { Button } from '@nextui-org/react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faInfo, faClose } from '@fortawesome/free-solid-svg-icons';

const StaticProjectCard = ({ title, content, bgBackgroundColor, className }) => {
    return (
        <div className={`card ${className}`}>
            <div className="card-inner" style={{ backgroundColor: bgBackgroundColor }}>
                <div className="card-back">
                    <h2 className="text-xl font-semibold mb-2">{title}</h2>
                    <p className="text-gray-700">{content}</p>
                </div>
            </div>
        </div>
    );
};

const FlippableProjectCard = ({ title, content, imageSrc, logoBackgroundColor, className }) => {
    const [flipped, setFlipped] = useState(false);

    return (
        <div
            className={`card ${flipped ? 'flipped' : ''} ${className}`}
            onClick={() => setFlipped(!flipped)}
        >
            <div className="card-inner">
                <div className="card-front p-5" style={{ backgroundColor: logoBackgroundColor }}>
                    <img src={imageSrc} alt={title} className="object-scale-down w-full h-full" />
                    <Button className='absolute bottom-2 right-2 rounded-full' size='sm' isIconOnly onClick={() => setFlipped(!flipped)} variant='faded' color='primary'>
                        <FontAwesomeIcon icon={faInfo} />
                    </Button>
                </div>
                <div className="card-back">
                    <h2 className="text-xl font-semibold mb-2 text-black">{title}</h2>
                    <p className="text-gray-700">{content}</p>
                    <Button className='absolute bottom-2 right-2 rounded-full' size='sm' isIconOnly onClick={() => setFlipped(!flipped)} variant='faded' color='primary'>
                        <FontAwesomeIcon icon={faClose} />
                    </Button>
                </div>
            </div>
        </div>
    );
};

const LTMS = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [currentImageIndex, setCurrentImageIndex] = useState(0);

    const images = [
        { original: require('../../../../assets/ltms/ltms1.png') },
        { original: require('../../../../assets/ltms/ltms2.png') },
        { original: require('../../../../assets/ltms/ltms3.png') },
        { original: require('../../../../assets/ltms/ltms4.png') },
        { original: require('../../../../assets/ltms/ltms5.png') },
        { original: require('../../../../assets/ltms/ltms6.png') }
    ];

    const handleImageClick = (index) => {
        setCurrentImageIndex(index);
        setIsOpen(true);
    };

    const closeModal = () => {
        setIsOpen(false);
    };

    return (
        <div className="h-screen grid grid-cols-5 grid-rows-5 gap-4 py-4 px-10">
            <StaticProjectCard
                title="LTMS"
                content="Details about project 1"
                bgBackgroundColor="#2A3766"
                className="col-span-3 row-span-2"
            />
            <FlippableProjectCard
                title=""
                content="Utilizzato sotto richiesta del cliente, imparato in autonomia"
                logoBackgroundColor="#f1f1f1"
                imageSrc={require('../../../../assets/aspnetmvc.webp')}
                className="col-span-1 row-span-1"
            />
            <FlippableProjectCard
                title=""
                content="Usato per la creazione di wireframes e mockups"
                logoBackgroundColor="#333"
                imageSrc={require('../../../../assets/figma.png')}
                className="col-span-1 row-span-1"
            />
            <FlippableProjectCard
                title=""
                content="Database disegnato e implementato da zero, in totale autonomia"
                logoBackgroundColor="#beface"
                imageSrc={require('../../../../assets/mssql.png')}
                className="col-span-1 row-span-1"
            />
            <FlippableProjectCard
                title="Pazienza"
                content="Comunicazione completa con il cliente, per capire le sue esigenze e soddisfarle"
                logoBackgroundColor="#2A3766"
                imageSrc={require('../../../../assets/devemoji.png')}
                className="col-span-1 row-span-1"
            />
            <StaticProjectCard
                title="Scritte"
                content="Details about project 6"
                bgBackgroundColor="#2A3766"
                className="col-span-2 row-span-3"
            />
            <div className="col-span-3 row-span-3">
                <ImageGallery
                    items={images}
                    showThumbnails={false}
                    showFullscreenButton={false}
                    showPlayButton={false}
                    onClick={(e, index) => handleImageClick(index)}
                />
            </div>
            <Modal
                isOpen={isOpen}
                onRequestClose={closeModal}
                contentLabel="Image Modal"
                className="modal"
                overlayClassName="overlay"
            >
                <button onClick={closeModal} className="close-button">
                    <FontAwesomeIcon icon={faClose} />
                </button>
                <img src={images[currentImageIndex].original} alt={`LTMS ${currentImageIndex + 1}`} className="modal-image" />
            </Modal>
        </div>
    );
};

export default LTMS;
