import React from 'react';
import { useState, useEffect } from 'react';
import { Navbar, NavbarBrand, NavbarNav, NavbarItem, NavbarToggler, NavbarCollapse } from '@nextui-org/react';
import { Container } from '@nextui-org/react';
const Nav = () => {
    const [active, setActive] = useState('');

    useEffect(() => {
        const path = window.location.pathname;
        setActive(path.slice(1));
    }, []);

    return (
        <Navbar color="light" expand="md">
                <NavbarBrand href="/">Home</NavbarBrand>
                <NavbarToggler />
                <NavbarCollapse>
                    <NavbarNav>
                        <NavbarItem active={active === 'chi_e'} href="/chi_e">Chi sono</NavbarItem>
                        <NavbarItem active={active === 'cosa_ha_fatto'} href="/cosa_ha_fatto">Portfolio</NavbarItem>
                        <NavbarItem active={active === 'curriculum'} href="/curriculum">Curriculum</NavbarItem>
                    </NavbarNav>
                </NavbarCollapse>
        </Navbar>
    )
};



export default Nav;
