import React, { useState } from 'react';
import './styles.css';
import { Button } from '@nextui-org/react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faInfo, faClose } from '@fortawesome/free-solid-svg-icons';
import ImageGallery from 'react-image-gallery';

const StaticProjectCard = ({ title, content, bgBackgroundColor, className }) => {
    return (
        <div className={`card ${className}`}>
            <div className="card-inner bg-gradient-to-r from-green-400 to-blue-500 shadow-lg rounded-lg overflow-hidden">
                <div className="card-front flex flex-col justify-center items-center p-8 text-center text-white">
                    <h1 className="text-5xl font-extrabold mb-4">{title}</h1>
                    <h3 className="text-2xl font-semibold mb-4">Launch Team Management System</h3>
                    <p className="text-lg mb-6">{content}</p>
                    <Button className="bg-white text-green-500 font-bold py-2 px-4 rounded-full hover:bg-green-500 hover:text-white transition duration-300">
                        Scopri di più
                    </Button>
                </div>
            </div>
        </div>
    );
};

const FlippableProjectCard = ({ title, content, imageSrc, logoBackgroundColor, className }) => {
    const [flipped, setFlipped] = useState(false);

    return (
        <div
            className={`card ${flipped ? 'flipped' : ''} ${className}`}
            onClick={() => setFlipped(!flipped)}
        >
            <div className="card-inner ">
                <div className="card-front p-5  bg-gradient-to-r from-gray-400 to-gray-600 shadow-lg rounded-lg" style={{ backgroundColor: logoBackgroundColor }}>
                    <img src={imageSrc} alt={title} className="object-scale-down w-full h-full" />
                    <Button className='absolute bottom-2 right-2 rounded-full' size='sm' isIconOnly onClick={() => setFlipped(!flipped)} variant='faded' color='primary'>
                        <FontAwesomeIcon icon={faInfo} />
                    </Button>
                </div>
                <div className="card-back">
                    <h2 className="text-xl font-semibold mb-2 text-black">{title}</h2>
                    <p className="text-gray-700">{content}</p>
                    <Button className='absolute bottom-2 right-2 rounded-full' size='sm' isIconOnly onClick={() => setFlipped(!flipped)} variant='faded' color='primary'>
                        <FontAwesomeIcon icon={faClose} />
                    </Button>
                </div>
            </div>
        </div>
    );
};

const LTMS = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [currentImageIndex, setCurrentImageIndex] = useState(0);
    const images = [
        {
            original: require('../../../../assets/ltms/ltms1.png'),
            thumbnail: require('../../../../assets/ltms/ltms1.png')
        },
        {
            original: require('../../../../assets/ltms/ltms2.png'),
            thumbnail: require('../../../../assets/ltms/ltms2.png')
        },
        {
            original: require('../../../../assets/ltms/ltms3.png'),
            thumbnail: require('../../../../assets/ltms/ltms3.png')
        },
        {
            original: require('../../../../assets/ltms/ltms4.png'),
            thumbnail: require('../../../../assets/ltms/ltms4.png')
        },
        {
            original: require('../../../../assets/ltms/ltms5.png'),
            thumbnail: require('../../../../assets/ltms/ltms5.png')
        },
        {
            original: require('../../../../assets/ltms/ltms6.png'),
            thumbnail: require('../../../../assets/ltms/ltms6.png')
        },
    ];

    const handleImageClick = (index) => {
        setCurrentImageIndex(index);
        setIsOpen(true);
    };

    const closeModal = () => {
        setIsOpen(false);
    };

    return (
        <div className="h-screen grid grid-cols-5 grid-rows-5 gap-4 py-4 px-10">
            <div className="card col-span-3 row-span-2">
                <div className="card-inner bg-gradient-to-r from-green-400 to-blue-500 shadow-lg rounded-lg overflow-hidden">
                    <div className="card-front flex flex-col justify-center items-center p-8 text-center text-white">
                        <h1 className="text-5xl font-extrabold mb-4">LTMS</h1>
                        <h3 className="text-2xl font-semibold mb-4">Launch Team Management System</h3>
                        <p className="text-lg mb-6">
                            Un gestionale di utenti con lato amministrativo e lato utente. Permette la gestione di ore di lavoro, attività, ordini, richieste d'acquisto, forecast, warroom, costi degli utenti per ora, mail periodiche ed export in Excel.
                        </p>
                        <Button className="bg-white text-green-500 font-bold py-2 px-4 rounded-full hover:bg-green-500 hover:text-white transition duration-300">
                            Scopri di più
                        </Button>
                    </div>
                </div>
            </div>
            <FlippableProjectCard
                title="ASP.NET MVC"
                content="Utilizzato sotto richiesta del cliente, imparato in autonomia"
                logoBackgroundColor="#f1f1f1"
                imageSrc={require('../../../../assets/aspnetmvc.webp')}
                className="col-span-1 row-span-1"
            />
            <FlippableProjectCard
                title="Figma"
                content="Usato per la creazione di wireframes e mockups"
                logoBackgroundColor="#333"
                imageSrc={require('../../../../assets/figma.png')}
                className="col-span-1 row-span-1"
            />
            <FlippableProjectCard
                title="Microsoft SQL Server"
                content="Database disegnato e implementato da zero, in totale autonomia"
                logoBackgroundColor="#beface"
                imageSrc={require('../../../../assets/mssql.png')}
                className="col-span-1 row-span-1"
            />
            <FlippableProjectCard
                title="Pazienza"
                content="Comunicazione completa con il cliente, per capire le sue esigenze e soddisfarle"
                logoBackgroundColor="#2A3766"
                imageSrc={require('../../../../assets/devemoji.png')}
                className="col-span-1 row-span-1"
            />
            <div className="card col-span-2 row-span-3">
                <div className="card-inner bg-gradient-to-r from-indigo-500 to-purple-600 shadow-lg rounded-lg overflow-hidden">
                    <div className="card-front flex flex-col justify-center items-center p-8 text-center text-white">
                        <h1 className="text-4xl font-extrabold mb-4">LTMS</h1>
                        <h3 className="text-2xl font-semibold mb-6">Launch Team Management System</h3>
                        <p className="text-lg mb-4">
                            <strong>Descrizione del progetto:</strong>
                        </p>
                        <p className="text-lg mb-6">
                            Un sistema gestionale per il management del team di lancio, che include:
                        </p>
                        <ul className="list-disc list-inside text-left text-lg space-y-2">
                            <li>Calendario per l'inserimento di ore di lavoro e attività lato utente.</li>
                            <li>Visualizzazione dati lato amministrativo/PM.</li>
                            <li>Gestione degli ordini e delle richieste d'acquisto.</li>
                            <li>Forecast e warroom.</li>
                            <li>Gestione dei costi degli utenti per ora.</li>
                            <li>Mail periodiche e export in Excel.</li>
                        </ul>
                    </div>
                </div>
            </div>
            <div className="col-span-3 row-span-3">
                <div className="card w-full h-full">
                    <div className="card-inner flex justify-center items-center p-5" style={{ backgroundColor: '#e8d954' }}>
                        <ImageGallery
                            items={images}
                            showPlayButton={false}
                            showFullscreenButton={true}
                            showBullets={true}
                            showThumbnails={false}
                            showIndex={false}
                            showNav={true}
                        />
                    </div>
                </div>
            </div>
        </div>
    );
};

export default LTMS;
